mapper.select:
SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_DEL_FL = 'N' 
AND MEMBER_EMAIL = #{memberEmail}

SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_DEL_FL = 'N' 
AND MEMBER_NICKNAME = #{memberNickname}

mapper.update:
UPDATE "MEMBER" SET
MEMBER_PASSWORD = #{memberPassword}
WHERE MEMBER_DEL_FL = 'N'
AND MEMBER_EMAIL = #{memberEmail}
