mapper.insert:

 			SELECT SEQ_MTM_NO.NEXTVAL FROM DUAL


 		INSERT INTO MTM
 		VALUES(
 			 #{mtmNo}
			,#{mtmTitle}
			,#{mtmContent}
			,DEFAULT
			,NULL
			,#{memberNo}
			,NULL
			,NULL
 		)

mapper.update:
 		UPDATE MTM SET
			 MTM_TITLE = #{mtmTitle}
			,MTM_CONTENT = #{mtmContent}
			,UPDATE_DATE = SYSDATE
 		WHERE MTM_NO = #{mtmNo}

UPDATE "MEMBER" SET
MEMBER_NAME = #{memberName},
MEMBER_NICKNAME = #{memberNickname},
MEMBER_TEL = #{memberTel},
MEMBER_ADDR = #{memberAddr}
WHERE MEMBER_NO = #{memberNo}
