mapper.select:
 		SELECT COUNT(*) FROM REVIEW
		WHERE REVIEW_DEL_FL = 'N' 
		AND ROOM_ID = #{roomId}

mapper.update:
UPDATE REVIEW SET 
REVIEW_TITLE = #{reviewTitle}, 
REVIEW_CONTENT = #{reviewContent}, 
REVIEW_GRADE = #{reviewGrade}
WHERE REVIEW_NO = #{reviewNo}
AND REVIEW_DEL_FL = 'N'

UPDATE REVIEW SET
REVIEW_DEL_FL = 'Y'
WHERE REVIEW_NO = #{reviewNo}
