mapper.insert:

	SELECT SEQ_ROOM_ID.NEXTVAL FROM DUAL


INSERT INTO "ROOM"
VALUES(
	 #{roomId}
	,#{roomName}
	,#{roomPrice}
	,NULL, NULL
	,#{roomInfo} 
	,'N'
)

INSERT INTO "UPLOAD_FILE"


	SELECT
		 NEXT_FILE_NO()
		,#{img.filePath}
		,#{img.fileOrgName}
		,#{img.fileRename}
		,SYSDATE
		,#{img.tableName}
		,#{img.tableNo}
		,#{img.thumbnail} 
	FROM DUAL

mapper.update:
UPDATE ROOM SET 
	 ROOM_NAME = #{roomName}
	,ROOM_PRICE = #{roomPrice}
	,ROOM_INFO = #{roomInfo} 
	,ROOM_DEL_FL ='N'
WHERE ROOM_ID = #{roomId}

UPDATE UPLOAD_FILE SET
	 FILE_ORG_NAME = #{fileOrgName}
	,FILE_RENAME = #{fileRename}
	,FILE_UPLOAD_DATE = SYSDATE
	,THUMBNAIL = #{thumbnail} 
WHERE FILE_NO = #{fileNo}
AND TABLE_NAME = 'ROOM'
AND TABLE_NO = #{tableNo}
